int lenOfLongSubarr(int A[], int N, int K) {
    int pref_sum = 0;
    int ans = 0;
    unordered_map<int, int> mp;

    for (int i = 0; i < N; i++) {
        pref_sum += A[i];
        if (pref_sum == K)
            ans = max(ans, i + 1);
        if (mp.find(pref_sum - K) != mp.end())
            ans = max(ans, i - mp[pref_sum - K]);
        if (mp.find(pref_sum) == mp.end())
            mp[pref_sum] = i;
    }
    return ans;
}
