int median(vector<vector<int>>& matrix, int R, int C) {
    int low = INT_MAX;
    int high = INT_MIN;
    int opt_cnt = (R * C + 1) / 2;
    int ans = -1;

    for (int i = 0; i < R; i++) {
        low = min(low, matrix[i][0]);
        high = max(high, matrix[i][C - 1]);
    }

    while (low <= high) {
        int mid = low + (high - low) / 2;
        int cnt = 0;
        for (int i = 0; i < R; i++) {
            cnt += upper_bound(matrix[i].begin(), matrix[i].end(), mid) - matrix[i].begin();
        }
        if (cnt < opt_cnt)
            low = mid + 1;
        else {
            ans = mid;
            high = mid - 1;
        }
    }

    return ans;
}

