string frequencySort(string s) {
    unordered_map<char, int> mp;
    for (auto c : s) {
        mp[c]++;
    }

    priority_queue<pair<int, char>> pq;
    for (auto it : mp) {
        pq.push({ it.second, it.first });
    }

    string ans = "";
    while (!pq.empty()) {
        auto curr = pq.top();
        pq.pop();
        ans.append(curr.first, curr.second);
    }

    return ans;
}

