int myAtoi(string s) {
    int i = 0;
    while (i < s.size() && s[i] == ' ')
        i++;
    if (i == s.size())
        return 0;
    bool sign = true; // indicates the number is positive

    if (s[i] == '-') {
        i++;
        sign = false;
    } else if (s[i] == '+')
        i++;

    long long ans = 0;
    while (i < s.size() && (0 <= s[i] - '0' && s[i] - '0' <= 9)) {
        long long digit = s[i] - '0';
        ans = (ans * 10) + digit;
        // handle overflow case
        if (ans > INT_MAX && sign)
            return INT_MAX;
        else if (ans > INT_MAX)
            return INT_MIN;
        i++;
    }
    ans = sign ? ans : -1 * ans;
    return (int) ans;
}

