class Queue {
private:
    std::stack<int> input;
    std::stack<int> output;

public:
    void enqueue(int x);
    int dequeue();
};

void Queue::enqueue(int x) {
    input.push(x);
}

int Queue::dequeue() {
    if (input.empty() && output.empty())
        return -1;

    if (output.empty()) {
        while (!input.empty()) {
            int temp = input.top();
            input.pop();
            output.push(temp);
        }
    }

    int dequeued = output.top();
    output.pop();

    return dequeued;
}

