int atMostK(vector<int>& nums, int k) {
    int start = 0, oddCnt = 0, ans = 0;
    for(int i = 0; i < nums.size(); i++) {
        if(nums[i] % 2 != 0)
            oddCnt++;
        while(oddCnt > k) {
            if(nums[start] % 2 != 0)
                oddCnt--;
            start++;
        }
        ans += i - start + 1;
    }
    return ans;
}

