bool checkValidString(string s) {
    int cmin = 0, cmax = 0;
    for (auto c : s) {
        if (c == '(') {
            cmax++;
            cmin++;
        } else if (c == ')') {
            cmax--;
            cmin = max(cmin - 1, 0);
        } else { // this is the case of '*'
            cmax++; // if we treat '*' as '('
            cmin = max(cmin - 1, 0); // if we treat '*' as ')'
        }
        if (cmax < 0) // this means even after treating all '*' as '(' we can't maintain balance
            return false;
    }
    return (cmin == 0);
}

