TreeNode* solve(int i, int ins, int ine, vector<int>& preorder, vector<int>& inorder) {
    if (i >= preorder.size() || ins > ine) return NULL;

    int loc = -1;
    for (int j = ins; j <= ine; j++) {
        if (inorder[j] == preorder[i])
            loc = j;
