        int siz = q.size();
        while (siz--) {
            auto p = q.front();
            q.pop();
            int x = p.first, y = p.second;
            if (x == n - 1 && y == n - 1) return lvl;
            
            for (int i = -1; i <= 1; i++) {
                for (int j = -1; j <= 1; j++) {
                    int nx = x + i, ny = y + j;
                    if (isValid(nx, ny, n, vis, grid)) {
                        q.push({nx, ny});
                        vis[nx][ny] = true;
                    }
                }
            }
        }
        lvl++;
    }
    return -1;
}

