class DisjointSet {
    vector<int> parent, size;

public:
    DisjointSet(int n) {
        parent.resize(n);
        size.resize(n, 1);
        for (int i = 0; i < n; i++)
            parent[i] = i;
    }

    int findUParent(int node) {
        if (parent[node] == node)
            return node;
        return parent[node] = findUParent(parent[node]);
    }

    void unionBySize(int u, int v) {
        int uP = findUParent(u), vP = findUParent(v);
        if (uP == vP)
            return;
        if (size[uP] > size[vP]) {
            parent[vP] = uP;
            size[uP] += size[vP];
        } else {
            parent[uP] = vP;
            size[vP] += size[uP];
        }
    }
};

int minimumSpanningTree(vector<vector<int>>& edges, int n) {
    DisjointSet djs(n);
    sort(edges.begin(), edges.end());

    int ans = 0;
    for (auto e : edges) {
        int wt = e[0], u = e[1], v = e[2];
        if (djs.findUParent(u) != djs.findUParent(v)) {
            ans += wt;
            djs.unionBySize(u, v);
        }
    }

    return ans;
}

