        } else {
            parent[uP] = vP;
            size[vP] += uPSize;
        }
    }

    int disconnected(int n) {
        int cnt = 0;
        for (int i = 0; i < n; i++) {
            if (parent[i] == i)
                cnt++;
        }
        return cnt;
    }
};

