class DisjointSet {
    vector<int> parent, size;

public:
    DisjointSet(int n) {
        size.resize(n, 1);
        parent.resize(n);
        for (int i = 0; i < n; i++) parent[i] = i;
    }

    int findUPar(int node) {
        if (parent[node] == node) return node;
        return parent[node] = findUPar(parent[node]);
    }

    void unionSize(int u, int v) {
        int up = findUPar(u), vp = findUPar(v);
        int us = size[up], vs = size[vp];
        if (us > vs) {
            parent[vp] = up;
            size[up] += vs;
        } else {
            parent[up] = vp;
            size[vp] += us;
        }
    }
};

bool isLand(int x, int y, vector<vector<int>>& grid) {
    return (x >= 0 && x < grid.size() && y >= 0 && y < grid[0].size() && grid[x][y] == 1);
}

vector<int> numOfIslands(int n, int m, vector<vector<int>>& operators) {
    vector<int> ans;
    int islands = 0;
    DisjointSet djs(n * m);
    vector<vector<int>> grid(n, vector<int>(m, 0));

    vector<int> dr = { -1, 1, 0, 0 };
    vector<int> dc = { 0, 0, -1, 1 };

    for (auto op : operators) {
        int x = op[0], y = op[1];
        if (grid[x][y] != 1) {
            grid[x][y] = 1;
            islands++;
            int id = x * m + y;
            for (int i = 0; i < 4; i++) {
                int nx = x + dr[i], ny = y + dc[i];
                if (isLand(nx, ny, grid)) {
                    int nid = nx * m + ny;
                    if (djs.findUPar(id) != djs.findUPar(nid)) {
                        djs.unionSize(id, nid);
                        islands--;
                    }
                }
            }
        }
        ans.push_back(islands);
    }
    return ans;
}

