class TrieNode{
public:
    TrieNode* child[26];
    bool isWord;

    TrieNode(){
        this->isWord = false;
        for(int i=0; i<26; i++){
            this->child[i] = NULL;
        }
    }
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }
    
    void insert(string word) {
        TrieNode* curr = root;
        for(int i=0; i<word.size(); i++){
            int ltr = word[i] - 'a';
            if(!curr->child[ltr])
                curr->child[ltr] = new TrieNode();
            curr = curr->child[ltr];
        }
        curr->isWord = true;
    }
    
    bool search(string word) {
        TrieNode* curr = root;
        for(int i=0; i<word.size(); i++){
            int ltr = word[i]-'a';
            if(!curr->child[ltr]) return false;
            curr = curr->child[ltr];
        }
        return curr && curr->isWord;
    }
    
    bool startsWith(string prefix) {
        TrieNode* curr = root;
        for(int i=0; i<prefix.size(); i++){
            int ltr = prefix[i]-'a';
            if(!curr->child[ltr]) return false;
            curr = curr->child[ltr];
        }
        return curr;
    }
};

