class TrieNode {
public:
    TrieNode* child[2];
    
    TrieNode() {
        for(int i = 0; i < 2; i++) {
            this->child[i] = NULL;
        }
    }
};

class Trie {
public:
    TrieNode* root;
    
    Trie() {
        root = new TrieNode();
    }
    
    void insert(int num) {
        TrieNode* curr = root;
        for(int i = 31; i >= 0; i--) {
            int bit = (num >> i) & 1;
            if(!curr->child[bit]) {
                curr->child[bit] = new TrieNode();
            }
            curr = curr->child[bit];
        }
    }
    
    int findMax(int num) {
        TrieNode* curr = root;
        int maxNum = 0;
        for(int i = 31; i >= 0; i--) {
            int bit = (num >> i) & 1;
            if(curr->child[1 - bit]) {
                maxNum = maxNum | (1 << i);
                curr = curr->child[1 - bit];
            } else {
                curr = curr->child[bit];
            }
        }
        return maxNum;
    }
};

class Solution {
public:
    int findMaximumXOR(vector<int>& nums) {
        Trie* tr = new Trie();
        for(auto i : nums) {
            tr->insert(i);
        }
        int ans = 0;
        for(auto i : nums) {
            int temp = tr->findMax(i);
            ans = max(ans, temp);
        }
        return ans;
    }
};

