vector<int> lps(string& s){
    int n = s.size();
    vector<int> pi(n);
    for(int i = 1; i < n; i++){
        int j = pi[i - 1];
        while(j > 0 && s[j] != s[i]) {
            j = pi[j - 1];
        }
        if(s[j] == s[i]) {
            j++;
        }
        pi[i] = j;
    }
    return pi;
}

int strStr(string haystack, string needle) {
    if(needle.empty()) {
        return 0;
    }
    string temp = needle + '$' + haystack;
    vector<int> lpsArray = lps(temp);
    for(int i = 0; i < lpsArray.size(); i++) {
        if(lpsArray[i] == needle.size()) {
            return i - 2 * needle.size();
        }
    }
    return -1;
}

