string shortestPalindrome(string s) {
    string rev = s;
    reverse(rev.begin(), rev.end());
    string temp = s + "$" + rev;
    vector<int> lps = kmp(temp);
    string toAdd = s.substr(lps.back());
    reverse(toAdd.begin(), toAdd.end());
    return toAdd + s;
}
